// Get Vonage payload
const payload = $json.body || {};

// Extract message details
const fromNumber = payload.from || 'unknown';
const messageBody = payload.text || '';
const messageSid = payload.message_uuid || '';

// Parse command from message
const message = messageBody.trim().toUpperCase();
const parts = message.split(' ');
const command = parts[0];
const args = parts.slice(1);

let parsedCommand = {
  command: '',
  action: '',
  path: '',
  destination: '',
  valid: false,
  error: ''
};

// Command handling
switch (command) {
  case 'LIST':
    // Join all args to handle multi-word folder names
    const listPath = args.length > 0 ? args.join(' ') : '/';
    parsedCommand = {
      command: 'LIST',
      action: 'list_files',
      path: listPath,
      destination: '',
      valid: true,
      error: ''
    };
    break;

  case 'DELETE':
    if (args.length >= 1) {
      // Join all args to handle multi-word filenames
      const fileName = args.join(' ');
      parsedCommand = {
        command: 'DELETE',
        action: 'delete_file',
        path: fileName,
        destination: '',
        valid: true,
        error: ''
      };
    } else {
      parsedCommand.error = 'DELETE command requires a filename. Example: DELETE MY RESUME.pdf';
    }
    break;

  case 'MOVE':
    if (args.length >= 2) {
      // Join all args except the last one for filename, last arg is destination
      const fileName = args.slice(0, -1).join(' ');
      const destination = args[args.length - 1];
      parsedCommand = {
        command: 'MOVE',
        action: 'move_file',
        path: fileName,
        destination: destination,
        valid: true,
        error: ''
      };
    } else {
      parsedCommand.error = 'MOVE command requires filename and destination. Example: MOVE My Resume.pdf folder123';
    }
    break;

  case 'SUMMARY':
    // Join all args to handle multi-word folder names
    const summaryPath = args.length > 0 ? args.join(' ') : '/';
    parsedCommand = {
      command: 'SUMMARY',
      action: 'summarize_folder',
      path: summaryPath,
      destination: '',
      valid: true,
      error: ''
    };
    break;

  case 'HELP':
    parsedCommand = {
      command: 'HELP',
      action: 'show_help',
      path: '',
      destination: '',
      valid: true,
      error: ''
    };
    break;

  default:
    parsedCommand.error = `Unknown command: ${command}. Send HELP for available commands.`;
}

// Return for next nodes
return {
  whatsapp: {
    from: fromNumber,
    messageSid: messageSid,
    originalMessage: messageBody
  },
  parsed: parsedCommand,
  timestamp: new Date().toISOString()
};

