// Prepare data for move operation
const fileData = $('Search File to Move').first().json;
const folderData = $('Search Destination Folder').first().json;

// Get the original parsed command data
const parsedCommand = $('Parse WhatsApp Command').first().json.parsed;

// Check if we have the parsed command data
if (!parsedCommand) {
  throw new Error('Cannot access parsed command data');
}

// Check if file was found
if (!fileData.files || fileData.files.length === 0) {
  throw new Error(`File '${parsedCommand.path}' not found in Google Drive`);
}

// Check if destination folder was found
if (!folderData.files || folderData.files.length === 0) {
  throw new Error(`Destination folder '${parsedCommand.destination}' not found. Please make sure the folder exists and is named exactly '${parsedCommand.destination}'`);
}

const file = fileData.files[0];
const destinationFolder = folderData.files[0];

// Additional validation to ensure we found a folder, not a file
if (!destinationFolder.name || destinationFolder.name.includes('.')) {
  throw new Error(`Destination '${parsedCommand.destination}' appears to be a file (${destinationFolder.name}), not a folder. Please specify a folder name.`);
}

// Debug logging
console.log('File found:', file.name, 'ID:', file.id);
console.log('Destination folder found:', destinationFolder.name, 'ID:', destinationFolder.id);
console.log('Current parent:', file.parents ? file.parents[0] : 'none');

// Return the data needed for the move operation
return {
  fileId: file.id,
  fileName: file.name,
  currentParentId: file.parents ? file.parents[0] : null,
  destinationFolderId: destinationFolder.id,
  destinationFolderName: destinationFolder.name,
  originalCommand: parsedCommand
};

// Determine if we're dealing with a file or folder and prepare appropriate query
const parsedCommand = $('Parse WhatsApp Command').first().json.parsed;
const searchResult = $('Search Summary Folder').first().json;

// Handle root directory case
if (parsedCommand.path === '/') {
  return {
    query: "mimeType!='application/vnd.google-apps.folder' and trashed=false",
    isFolder: true,
    targetName: 'Root Directory'
  };
}

// Check if we found any results
if (!searchResult.files || searchResult.files.length === 0) {
  throw new Error(`No file or folder named '${parsedCommand.path}' found`);
}

const foundItem = searchResult.files[0];
const isFolder = foundItem.mimeType === 'application/vnd.google-apps.folder';

if (isFolder) {
  // It's a folder - get all files inside it
  return {
    query: `'${foundItem.id}' in parents and mimeType!='application/vnd.google-apps.folder' and trashed=false`,
    isFolder: true,
    targetName: foundItem.name,
    targetId: foundItem.id
  };
} else {
  // It's a single file - search by name to be safer with special characters
  const escapedName = foundItem.name.replace(/'/g, "\\'");
  return {
    query: `name='${escapedName}' and mimeType!='application/vnd.google-apps.folder' and trashed=false`,
    isFolder: false,
    targetName: foundItem.name,
    targetId: foundItem.id,
    mimeType: foundItem.mimeType,
    // For single files, we'll return the file data directly
    fileData: foundItem
  };
}

