// Process files and prepare for AI summary (handles both single files and folders)
const files = $input.all();
let summaryPrompts = [];

// Define supported text-based file types
const textMimeTypes = [
  'application/pdf',
  'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
  'application/msword',
  'text/plain',
  'application/vnd.google-apps.document'
];

for (let file of files) {
  if (file.json) {
    // Handle case where we get a list of files (folder contents)
    if (file.json.files && Array.isArray(file.json.files)) {
      for (let driveFile of file.json.files) {
        if (textMimeTypes.includes(driveFile.mimeType)) {
          summaryPrompts.push({
            fileId: driveFile.id,
            fileName: driveFile.name,
            mimeType: driveFile.mimeType,
            size: driveFile.size
          });
        }
      }
    }
    // Handle case where we get a single file directly
    else if (file.json.id && file.json.name && file.json.mimeType) {
      if (textMimeTypes.includes(file.json.mimeType)) {
        summaryPrompts.push({
          fileId: file.json.id,
          fileName: file.json.name,
          mimeType: file.json.mimeType,
          size: file.json.size
        });
      }
    }
  }
}

// If no summarizable files found, return helpful message
if (summaryPrompts.length === 0) {
  throw new Error('No summarizable files found. Supported formats: PDF, Word docs, Google Docs, Text files');
}

return summaryPrompts.map(file => ({
  json: {
    fileInfo: file,
    needsContent: true
  }
}));

// Process Gemini API response and extract the generated text
const response = $input.first().json;

// Gemini API returns response in format: { candidates: [{ content: { parts: [{ text: "..." }] } }] }
let summary = '';

if (response.candidates && response.candidates.length > 0) {
  const candidate = response.candidates[0];
  if (candidate.content && candidate.content.parts && candidate.content.parts.length > 0) {
    summary = candidate.content.parts[0].text || '';
  }
}

// If no summary found, provide fallback
if (!summary) {
  summary = 'Unable to generate summary from the document content.';
}

// Return in format expected by downstream nodes (compatible with OpenAI format)
return {
  json: {
    summary: summary.trim(),
    model: 'gemini-1.5-flash',
    timestamp: new Date().toISOString()
  }
};

// Format error response
const errorMessage = $json.parsed.error;
const friendlyError = `âŒ *Command Error*

${errorMessage}

Send *HELP* to see available commands and syntax.

*Quick Examples:*
â€¢ LIST /
â€¢ SUMMARY /MyFolder
â€¢ HELP`;

return {
  response: friendlyError,
  messageType: 'error'
};

// Format file list response
const files = $input.all();
let fileList = '';
let folderCount = 0;
let fileCount = 0;

// Get the path from the original command (from Route Command node)
const originalPath = $('Parse WhatsApp Command').item.json.parsed.path || '/';

if (files.length > 0 && files[0].json && files[0].json.files) {
  const driveFiles = files[0].json.files;

  if (driveFiles.length === 0) {
    fileList = 'ðŸ“‚ *Folder is empty*';
  } else {
    fileList = `ðŸ“ *Files in ${originalPath}:*

`;

    // Sort files: folders first, then files
    const sorted = driveFiles.sort((a, b) => {
      if (a.mimeType === 'application/vnd.google-apps.folder' && b.mimeType !== 'application/vnd.google-apps.folder') return -1;
      if (a.mimeType !== 'application/vnd.google-apps.folder' && b.mimeType === 'application/vnd.google-apps.folder') return 1;
      return a.name.localeCompare(b.name);
    });

    for (let file of sorted.slice(0, 20)) { // Limit to 20 items
      const isFolder = file.mimeType === 'application/vnd.google-apps.folder';
      const icon = isFolder ? 'ðŸ“' : 'ðŸ“„';
      const size = isFolder ? '' : ` (${formatFileSize(file.size || 0)})`;

      fileList += `${icon} ${file.name}${size}
`;

      if (isFolder) folderCount++;
      else fileCount++;
    }

    if (driveFiles.length > 20) {
      fileList += `
... and ${driveFiles.length - 20} more items`;
    }

    fileList += `

ðŸ“Š *Summary:* ${folderCount} folders, ${fileCount} files`;
  }
} else {
  fileList = 'âŒ Could not access folder. Check the path and try again.';
}

function formatFileSize(bytes) {
  if (bytes === 0) return '0 B';
  const k = 1024;
  const sizes = ['B', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
}

return {
  response: fileList,
  messageType: 'list'
};

// Format delete confirmation response
const originalPath = $('Parse WhatsApp Command').item.json.parsed.path;
const fileName = originalPath.split('/').pop() || 'Unknown file';
const deleteMessage = `ðŸ—‘ï¸ *File Deleted Successfully*

ðŸ“„ File: ${fileName}
ðŸ“ Path: ${originalPath}
â° Time: ${new Date().toLocaleString()}

âœ… The file has been permanently removed from your Google Drive.

*Note:* Deleted files may still be recoverable from Google Drive's trash for 30 days.`;

return {
  response: deleteMessage,
  messageType: 'delete'
};

// Format move confirmation response
// Get the original command data from Prepare Move Data node
const moveData = $('Prepare Move Data').first().json;
const fileName = moveData.fileName;
const originalCommand = moveData.originalCommand;

const moveMessage = `ðŸ“¦ *File Moved Successfully*

ðŸ“„ File: ${fileName}
ðŸ“ From: ${originalCommand.path}
ðŸ“ To: ${originalCommand.destination}
â° Time: ${new Date().toLocaleString()}

âœ… The file has been moved to the new location.`;

return {
  response: moveMessage,
  messageType: 'move'
};

// Combine all AI summaries into one response
const summaries = $input.all();
let summaryText = `ðŸ“„ *AI Summary for ${$('Parse WhatsApp Command').item.json.parsed.path || '/'}:*

`;

if (summaries.length === 0) {
  summaryText += 'ðŸ“‚ No summarizable documents found in this folder.

*Supported formats:* PDF, Word docs, Google Docs, Text files';
} else {
  summaries.forEach((item, index) => {
    // Handle both Gemini (new format) and OpenAI (legacy format) responses
    let fileName = 'Document';
    let summary = '';

    // Try to get filename from various possible sources
    if (item.json.fileInfo && item.json.fileInfo.fileName) {
      fileName = item.json.fileInfo.fileName;
    } else if (item.json.fileName) {
      fileName = item.json.fileName;
    }

    // Try to get summary from various possible sources
    if (item.json.summary) {
      summary = item.json.summary; // Gemini processed format
    } else if (item.json.choices && item.json.choices[0] && item.json.choices[0].message) {
      summary = item.json.choices[0].message.content; // OpenAI format
    } else {
      summary = 'Summary not available';
    }

    summaryText += `ðŸ“„ **${fileName}**
${summary}

`;
  });

  const model = summaries[0].json.model || 'AI';
  summaryText += `
ðŸ¤– *Generated by ${model}* â€¢ ${summaries.length} document(s) summarized`;
}

return {
  response: summaryText,
  messageType: 'summary'
};

// Debug: Log the data being sent to Vonage
console.log('WhatsApp data:', JSON.stringify($json.whatsapp, null, 2));
console.log('Response text:', $json.response);
console.log('Environment check:');
console.log('VONAGE_API_KEY exists:', !!process.env.VONAGE_API_KEY);
console.log('VONAGE_API_SECRET exists:', !!process.env.VONAGE_API_SECRET);

return $json;
